mapper.update:
update board
set view_cnt = view_cnt + 1
where brd_id = #{brdId}

update mvcboard
set downcount = downcount + 1
where idx = #{idx}

mapper.select:
select COUNT(*)
from mvcboard
where pass = #{pass} and idx = #{idx}

mapper.update:
update mvcboard
set title = #{title}, name = #{name},
content = #{content}, ofile = #{ofile}, sfile = #{sfile}
where idx = #{idx} and pass = #{pass}
