mapper.update:
update board
set view_cnt = view_cnt + 1
where brd_id = #{brdId}

mapper.select:
select COUNT(*)
from board
where brd_id = #{brdId} and user_id = #{userId}

mapper.update:
update board
set
title = #{title}, content = #{content},
ofile = #{ofile}, sfile = #{sfile},
cate_sub = #{cateSub}
where brd_id = #{brdId}

update product
set
    price = #{price},
    status = #{status},
    deal_address = #{dealAddress}

    , buyer_id = #{buyerId}

where brd_id = #{brdId}
